pluginOptions.query:
{
  site {
    siteMetadata {
      rssMetadata {
        site_url
        feed_url
        title
        description
        image_url
        author
        copyright
      }
    }
  }
}

feeds.query:
{
  allMarkdownRemark(
    limit: 1000,
    sort: { order: DESC, fields: [frontmatter___date] },
  ) {
    edges {
      node {
        excerpt(pruneLength: 400)
        html
        id
        fields { slug }
        frontmatter {
          title
          templateKey
          cover
          date(formatString: "MMMM DD, YYYY")
          tags
        }
      }
    }
  }
}
